import React from "react";
import { cn } from "../lib/utils";
// Fixed: Use correct component names as per usage in the file
import Image from "next/image"
import { Swiper, SwiperSlide } from "swiper/react"

import "swiper/css"
import "swiper/css/effect-coverflow"
import "swiper/css/pagination"
import "swiper/css/navigation"
import { SparklesIcon } from "lucide-react"
import {
  Autoplay,
  EffectCoverflow,
  Navigation,
  Pagination,
} from "swiper/modules"

import { Badge } from "@/components/ui/badge"

interface CarouselProps {
  images: { src: string; alt: string }[]
  autoplayDelay?: number
  showPagination?: boolean
  showNavigation?: boolean
}
import SkiperNavbar from "../components/SkiperNavbar";
import CardCarousel from "../components/CardCarousel";
import SkiperCard from "../components/Card";

const Home: React.FC = () => {
  const pageWrapper: React.CSSProperties = {
    minHeight: "100vh",
    backgroundColor: "#f4f4f4",
    color: "#333",
    fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
  };

  const heroStyle: React.CSSProperties = {
    textAlign: "center",
    padding: "60px 20px",
  };

  const storeCardsContainer: React.CSSProperties = {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(240px, 1fr))",
    gap: "20px",
    padding: "20px",
  };

  return (
    <div style={pageWrapper}>
      {/*  Navbar via Skiper-UI */}
      <SkiperNavbar />

      {/*  Hero Section */}
      <div style={heroStyle}>
        <h1 className="text-4xl font-bold">Discover Great Stores Around You</h1>
        <p className="mt-4 text-lg">Rate & review, help others shop smarter.</p>
      </div>

      {/*  Slider / Card Carousel */}
      <div className="mb-8">
        <CardCarousel>
          <SkiperCard title="Featured Store 1" description="Amazing deals" />
          <SkiperCard title="Featured Store 2" description="Top rated" />
          <SkiperCard title="Featured Store 3" description="New in town" />
        </CardCarousel>
      </div>

      {/*  Store List */}
      <div style={storeCardsContainer}>
        {[
          { name: "Coffee Hub", location: "Pune", stars: "⭐⭐⭐⭐☆" },
          { name: "Book Nook", location: "Mumbai", stars: "⭐⭐⭐☆☆" },
          { name: "Techies", location: "Kolhapur", stars: "⭐⭐⭐⭐⭐" },
        ].map((store, idx) => (
          <div key={idx} className={cn("bg-white p-4 shadow rounded-lg")}>
            <h3 className="text-xl font-semibold">{store.name}</h3>
            <p className="text-gray-600">{store.location}</p>
            <div className="mt-2 text-2xl text-yellow-500">{store.stars}</div>
          </div>
        ))}
      </div>

      {/*  Footer */}
      <footer className="bg-gray-800 text-white text-center py-6">
        © 2025 StoreRatings. All rights reserved.
      </footer>
    </div>
  );
};

export default Home;
