import React, { useState, useRef, useEffect } from 'react';
import Dashboard from '../components/Admin/Dashboard';
import UsersList from '../components/Admin/UsersList';
import StoresList from '../components/Admin/StoresList';
import gsap from 'gsap';

const tabs = [
  { id: 'dashboard', label: 'Dashboard' },
  { id: 'users', label: 'Users List' },
  { id: 'stores', label: 'Stores List' },
];

const AdminPage: React.FC = () => {
  const [activeTab, setActiveTab] = useState<string>('dashboard');
  const contentRef = useRef<HTMLDivElement>(null);

  const handleTabChange = (tabId: string) => {
    setActiveTab(tabId);
  };

  // Animate content on tab change
  useEffect(() => {
    if (contentRef.current) {
      gsap.fromTo(
        contentRef.current,
        { opacity: 0, y: 20 },
        { opacity: 1, y: 0, duration: 0.5, ease: 'power3.out' }
      );
    }
  }, [activeTab]);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-bold mb-6 text-gray-800 text-center">Admin Panel</h1>

      {/* Tabs */}
      <div className="flex justify-center space-x-4 mb-6">
        {tabs.map((tab) => (
          <button
            key={tab.id}
            className={`px-6 py-2 rounded-lg font-medium transition ${
              activeTab === tab.id
                ? 'bg-blue-500 text-white shadow-lg'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
            onClick={() => handleTabChange(tab.id)}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {/* Tab Content */}
      <div ref={contentRef} className="bg-white p-6 rounded-xl shadow-lg">
        {activeTab === 'dashboard' && <Dashboard />}
        {activeTab === 'users' && <UsersList />}
        {activeTab === 'stores' && <StoresList />}
      </div>
    </div>
  );
};

export default AdminPage;
