import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import StoreList from '../components/User/StoreList';
import RatingForm from '../components/User/RatingForm';

type Store = {
    id: number;
    name: string;
    // add other store properties if needed
};

const UserPage = () => {
    const [stores, setStores] = useState<Store[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedStoreId, setSelectedStoreId] = useState<number | null>(null);

    useEffect(() => {
        const fetchStores = async () => {
            try {
                const response = await axios.get('/stores');
                setStores(response.data);
            } catch (err) {
                setError('Failed to fetch stores');
            } finally {
                setLoading(false);
            }
        };

        fetchStores();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="user-page p-6">
            <h1 className="text-2xl font-bold text-center mb-6">Browse Stores</h1>

            {/* Store List Component */}
            <StoreList stores={stores as any} onSelectStore={setSelectedStoreId} />

            {/* Conditionally render RatingForm */}
            {selectedStoreId && (
                <div className="mt-8">
                    <RatingForm storeId={selectedStoreId} />
                </div>
            )}
        </div>
    );
};

export default UserPage;
