import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import StoreList from '../components/User/StoreList';
import RatingForm from '../components/User/RatingForm';
import useAuth from '../hooks/useAuth';

// Define Store type locally since it's not exported from index.ts
export interface Store {
    id: number;
    name: string;
    address: string;
    rating: number;
    // add other fields as needed
}

const UserPage = () => {
    const { user, loading: authLoading } = useAuth();
    const [stores, setStores] = useState<Store[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedStoreId, setSelectedStoreId] = useState<number | null>(null);

    useEffect(() => {
        if (!authLoading && user) {
            const fetchStores = async () => {
                try {
                    const response = await axios.get('/stores');
                    // Ensure the response data matches the Store interface
                    const storesData: Store[] = response.data.map((store: any) => ({
                        id: store.id,
                        name: store.name,
                        address: store.address,
                        rating: store.rating,
                        // add other fields as needed
                    }));
                    setStores(storesData);
                } catch (err) {
                    setError('Failed to fetch stores');
                } finally {
                    setLoading(false);
                }
            };
            fetchStores();
        } else if (!authLoading && !user) {
            setLoading(false);
            setError('You must be logged in to view this page.');
        }
    }, [user, authLoading]);

    if (authLoading) return <div>Loading user authentication...</div>;
    if (loading) return <div>Loading store data...</div>;
    if (error) return <div className="text-red-500 text-center mt-10 text-xl font-bold">{error}</div>;

    return (
        <div className="user-page p-6">
            <h1 className="text-2xl font-bold text-center mb-6">Browse Stores</h1>
            <StoreList stores={stores} setSelectedStoreId={setSelectedStoreId} />
            {selectedStoreId && (
                <div className="mt-8">
                    <RatingForm storeId={selectedStoreId} />
                </div>
            )}
        </div>
    );
};

export default UserPage;
